import  { useContext, useEffect } from "react";
import { ThemeContext, themes } from "./utils/global.context";

const BodyStyles = () => {
  const { theme } = useContext(ThemeContext);

  useEffect(() => {
    // Cambiar el fondo del body basado en el tema
    document.body.style.backgroundColor =
      theme === themes.light ? "#fff" : "#000";

    // Asegúrate de limpiar los estilos cuando el componente se desmonta
    return () => {
      document.body.style.backgroundColor = "";
    };
  }, [theme]);

  // Puedes devolver null ya que este componente no renderiza nada en la interfaz
  return null;
};

export default BodyStyles;
