import { createContext, useState, useEffect, useReducer } from "react";
import axios from "axios";
export const initialState = {theme: "", data: []}

export const ContextGlobal = createContext(undefined);

// Crear el contexto
export const ThemeContext = createContext();

// Definir los temas
export const themes = {
  light: {
    font: 'black',
    background: 'white'
  },
  dark: {
    font: 'white',
    background: 'black'
  }
};

// Acciones para el useReducer
const TOGGLE_THEME = 'TOGGLE_THEME';

// Reducer para manejar cambios de tema
const themeReducer = (state, action) => {
  switch (action.type) {
    case TOGGLE_THEME:
      return state === themes.light ? themes.dark : themes.light;
    default:
      return state;
  }
};

export const ContextProvider = ({ children }) => {
  //Aqui deberan implementar la logica propia del Context, utilizando el hook useMemo
  const [datos, setDatos] = useState([]);

  useEffect(() => {
      getData();
  }, []);

  const getData = async () => {
      try {
          const urlApi = "https://jsonplaceholder.typicode.com/users";
          const resp = await axios.get(urlApi);
          console.log(resp)
          setDatos(resp.data);
      } catch (error) {
          console.log(error);
      }
  };

  return (
    <ContextGlobal.Provider 
    value={{
        getData,
        datos
    }}>
      {children}
    </ContextGlobal.Provider>
  );
};

// Componente proveedor del contexto que utiliza useReducer
export const ThemeProvider = ({ children }) => {
  const [theme, dispatch] = useReducer(themeReducer, themes.light);

  // Función para cambiar el tema
  const toggleTheme = () => {
    dispatch({ type: TOGGLE_THEME });
  };

  return (
    <ThemeContext.Provider value={{ theme, toggleTheme }}>
      {children}
    </ThemeContext.Provider>
  );
};
