import React from "react";
import { useState } from "react";

const Form = () => {
  const [nombre, setNombre] = useState('');
  const [email, setEmail] = useState('');
  const [isValid, setIsValid] = useState(null);

  const handleSubmit = (e) => {
    e.preventDefault();

    // Validaciones
    const isNombreValid = nombre.length > 5 && nombre.trim() && isNaN(nombre);
    const isNombreValid2 = isNaN(nombre);
    const isEmailValid = /^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(email);

    // Actualizar el estado es valido
    setIsValid(isNombreValid && isEmailValid && isNombreValid2);

    setTimeout(() => {
      setNombre('');
      setEmail('');
    }, 2000);
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <label>
          Nombre completo:
          <input
            type="text"
            name="name"
            value={nombre}
            onChange={(e) => setNombre(e.target.value)}
          />
        </label>
        <br />
        <label>
          Email:
          <input
            type="email"
            name="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
        </label>
        <br />
        <button type="submit">Enviar</button>
      </form>

      {/* Mostrar mensajes según el estado del formulario */}
      {isValid !== null && (
        isValid ? (
          <p>Mensaje positivo: ¡Formulario válido y enviado con éxito!</p>
        ) : (
          <p>Mensaje de error: Por favor, completa los campos correctamente.</p>
        )
      )}
    </div>
  );
};

export default Form;
