import React from "react";
import { useEffect, useState, useContext } from "react";
import Card from "../Components/Card";
import { ThemeContext, themes } from "../Components/utils/global.context";

//Este componente debera ser estilado como "dark" o "light" dependiendo del theme del Context

const Favs = () => {
  const { theme } = useContext(ThemeContext);
  const themeClass = theme === themes.light ? 'light' : 'dark';
  const [favCards, setFavCards] = useState([]);

  useEffect(() => {
    // Obtener los datos del localStorage al cargar la página
    const storedFavs = JSON.parse(localStorage.getItem("favCards")) || [];
    setFavCards(storedFavs);
  }, []);

  return (
    <>
    <main className={`${themeClass}`}>
      <h1>Dentists Favs</h1>
        <div className="card-grid">
          {favCards.map((fav) => (
            <div className="card" key={fav.id}>
              {/* Reutilizar el componente Card y pasar las propiedades */}
              <Card name={fav.name} username={fav.username} id={fav.id} />
            </div>
          ))}
        </div>
    </main>
    </>
  );
};

export default Favs;
