import  { useContext, useEffect } from "react";
import { ThemeContext, themes } from "./utils/global.context";

const BodyStyles = () => {
  const { theme } = useContext(ThemeContext);

  useEffect(() => {
    // Cambiar la clase del body basado en el tema
    document.body.classList.remove("light-theme", "dark-theme");
    document.body.classList.add(theme);

    // Asegúrate de limpiar las clases cuando el componente se desmonta
    return () => {
      document.body.classList.remove("light-theme", "dark-theme");
    };
  }, [theme]);

  // Puedes devolver null ya que este componente no renderiza nada en la interfaz
  return null;
};


export default BodyStyles;
